        <link rel="stylesheet" href="../style/style.css">

        <!-- Favicon -->
        <link rel="icon" href="../assets/favicon.jpg" />
        <title>Fil rouge | Connection</title>
    </head>

    <body class="px-3">
        <header class="py-4">
            <nav>
                <a type="button" class="button me-3" data-bs-toggle="modal" data-bs-target="#exampleModal1">
                    Sign In
                </a>
                <a type="button" class="button" data-bs-toggle="modal" data-bs-target="#exampleModal2">
                    Sign Up
                </a>
            </nav>
        </header>
        
        <main class="container rounded p-2 my-2 shadow-sm">

            <!-- Modal Login -->
            <section class="modal fade" id="exampleModal1" tabindex="-1" aria-labelledby="exampleModalLabel" aria-hidden="true">
                <div class="modal-dialog">
                    <div class="modal-content">
                        <div class="modal-body">
                            <div class="modal-header">
                                <h2 class="modal-title">Sign in</h2>
                                <button type="button" class="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
                            </div>
                            <form id="connexionForm" method="POST" class="pt-4">
                                <div class="pb-2">
                                    <label for="login_user">Login</label>
                                    <input class="w-100" type="text" name="login_user" required>
                                </div>
                                <div>
                                    <label for="pw_user">Password</label>
                                    <input class="w-100" type="password" name="password_user" required>
                                </div>
                                <div class="modal-footer mt-4">
                                    <button class="button" type="submit" value="Send">Log in</button>
                                </div>
                            </form>
                        </div>
                    </div>
                </div>
            </section>

            <!-- Register form -->
            <section class="modal fade" id="exampleModal2" tabindex="-1" aria-labelledby="exampleModalLabel" aria-hidden="true">
                <div class="modal-dialog">
                    <div class="modal-content">
                        <div class="modal-header">
                            <h2 class="modal-title" id="exampleModalLabel">Sign Up</h2>
                            <button type="button" class="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
                        </div>
                        <div class="modal-body">
                            <form id="registerForm" method="POST">
                                <div class="pb-2">
                                    <label for="name_user">Name</label>
                                    <input class="w-100" type="text" name="name_user" value="Test1111" required>
                                </div>
                                <div class="pb-2">
                                    <label for="first_name_user">First name</label>
                                    <input class="w-100" type="text" name="first_name_user" value="Test1111" required>
                                </div>
                                <div class="pb-2">
                                    <label for="login_user">Login</label>
                                    <input class="w-100" type="text" name="login_user" value="Test111" required>
                                </div>
                                <div class="pb-2">
                                    <label for="email_user">Email</label>
                                    <input class="w-100" type="email" name="email_user" value="azerty@azerty.fr" required>
                                </div>
                                <div class="pb-2">
                                    <label for="email_user">Confirm your email</label>
                                    <input class="w-100" type="email" name="em2_user" value="azerty@azerty.fr" required>
                                </div>
                                <div class="pb-2">
                                    <label for="pw_user">Password</label>
                                    <input class="w-100" type="password" name="password_user" value="Azerty1!" required>
                                </div>
                                <div>
                                    <label for="pwd2_user">Confirm your password</label>
                                    <input class="w-100" type="password" name="pwd2_user" value="Azerty1!" required>
                                </div>
                                <div class="modal-footer">
                                    <button class="button" type="submit" value="Send" required>Send</button>
                                </div>
                            </form>
                        </div>
                    </div>
                </div>
            </section>

